import React from "react";
import { Col, Jumbotron, Button } from "reactstrap";

const Product = ({ price }) => {
    const discount = false;
    const numberDaysUntilEndDiscount = 4;
    return (
      <Col>
        <Jumbotron>
          <img src="/static/product1.png" alt="product1" className="product__image" />
          <h2>Product Name</h2>
          <p>Product description</p>
          <span>{price}</span>
          {discount && <span>discount</span>}
          {numberDaysUntilEndDiscount && <span>{numberDaysUntilEndDiscount}</span>}
          <Col className="d-flex justify-content-around">
            <Button
              type="button" color="danger" onClick={() => alert("remove")} className="text-uppercase w-50"
            >remove</Button>
            <Button
              type="button" color="warning" onClick={() => alert("edit")} className="text-uppercase w-50"
              >
              edit
            </Button>
          </Col>
        </Jumbotron>
      </Col>
    );
  }

  export default Product;
